
public class NestedForLoop1 {

	public static void main(String[] args) {
		int k = 1; // initialization
		// TODO Auto-generated method stub
		//int n = 1;
		// first execute do body pat than check condtion
		do {
		System.out.println("Amr");	//implemation   
		k++;// expression
		} while (k <1);// condition
		
		
		while (k<=2) {
			System.out.println("Amroooooooooooooooooooooo");	//implemation   
			k++;// expression
		}
		
		
		int counter1=1;
		for (int i = 1;i<1;i++)
		{
			System.out.println(counter1+  " Amrish");
			counter1 ++;
			int counter=1 ;
			for(int j=1;j<=5;j++)
			{
				System.out.println(counter+  " Jagamohan");
				counter++;
			}
		
		}

	}

}
