
public class StrickDoWhile1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1,j=1,num=5;
		do {
	
			do {
				System.out.print("*");
				
	            j++;
	            
			} while (j <= i);
			i++;
			j=1;
			System.out.print("\n");
		} while (i <= num);
        
		}
        
		}
	

